    terminal.printLine(`command not found: ${commandName}`)

    if (distances[bestMatch] <= 2) {
        terminal.print("did you mean: ")
        terminal.printCommand(`${bestMatch}${commandArgs}`, `${bestMatch}${commandArgs}`)
    }
