    const promptLn = async (msg, regex, defaultValue=null) => {
        if (defaultValue) {
            msg += ` (default: ${defaultValue}): `
        } else {
            msg += ": "
        }

        while (true) {
            let result = await terminal.prompt(msg)
            if (result == "" && defaultValue) {
                return defaultValue
            } else if (regex.test(result)) {
                return result
            } else {
                await printLn("Invalid input. Please try again.")
            }
        }
    }

    let chat = new Chat()

